import React from 'react'
import { formatRelative } from 'date-fns'
import styled from 'styled-components';

const GuestBookList = ({thought}) => {

    return (
    <GuestBookListWrapper>
         <form className='message-card' key={thought._id} >
            <p className='messages'>{thought.name}</p>
            <p className='messages'>{thought.message}</p>
            <div className="likedate-wrapper">
                <p className="date"> {formatRelative(new Date(thought.createdAt), new Date())}</p>
            </div>
        </form>   
    </GuestBookListWrapper>
    );
};

export default GuestBookList

const GuestBookListWrapper = styled.section`
  border: 2px solid rgb(165, 165, 165);
  display: flex;
  align-items: center;
  justify-content: center;
  height: 150px;
  width: 400px;
  background-color: white;
  margin-top: 20px;
  box-shadow: 6px 6px;
