import React from 'react';
import Logo from 'images/prinscarl_logo.png'
import Phone from 'icons/phone.png'
import styled from 'styled-components'
import { useTranslation } from "react-i18next";
import NavigationPhone from './NavigationPhone';

const Header = () => {
const { t } = useTranslation();

    return (
    <>
    <NavigationPhone/>
    <NavMenu>
    <a href="/"><Image src={Logo} alt="Prins Carl Logo"/></a>
    <a href="/info"><ContactButton>Contact us</ContactButton></a>
    <input type="checkbox" id="checkbox_toggle" />
    <label htmlFor="checkbox_toggle" className="hamburger">&#9776;</label>
    <div className="menu">
      <div className="linkwrapper">
      <a href="/book">Book a room |</a>
      <a href="/info">Contact & information |</a>
      <a href="/breakfast">Breakfast |</a>
      <a href="/Events">Events |</a>
      <a href="#">Photoalbum |</a>
      <a href="/aboutus">About us |</a>
      <a href="/guestbook">Guestbook |</a>
      </div>
    </div>
    <Call><PhoneIcon src={Phone} alt="Prins Carl Logo"/>040-040404</Call>
    </NavMenu>
    </>
    )
}

export default Header;

const ContactButton = styled.button`
    display: block;
    align-items: center;
    position: absolute;
    background-color:#646f85;
    top: 18px;
    right:55px;
    border: none;
    color: white;
    font-size: 18px;
    font-family: 'Playfair Display', serif;
    &:hover {
    cursor: pointer;
    }
    @media (min-width: 1020px) {
    display: none;
    }
`
const Call = styled.button`
display: none;
@media (min-width: 1020px) {
display: unset;
display: flex;
align-items: flex-end;
height: 70px;
width: 190px;
