background-color: white;
margin-right: 40px;
border: none;
font-family: 'PT Sans', sans-serif;
font-size: 20px;
&:hover {
    font-family: 'Playfair Display', serif;
    font-size: 21px;
    }
}
`

const Image = styled.img`
    display: block;
    align-items: center;
    position: absolute;
    height: 50px;
    top: 10px;
    left: 13px;
    border: none;
    color: white;
    font-family: 'Playfair Display', serif;
    &:hover {
    cursor: pointer;
    }
    @media (min-width: 1020px) {

    }
`

const PhoneIcon = styled.img`
display: none;
height: 20px;
margin-left: 10px;
padding-top: 5px;
@media (min-width: 1020px) {
display: unset;
height: 45px;
}
`

const NavMenu = styled.nav`
    border: 2px solid yellow;
    background-color: white;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: flex-end;
    width: 100%;
    height: 55px;

//DESKTOP
    @media (min-width: 1020px) {
    background-color: white;
    width: 100%;
    height: 95px;
    }

    .linkwrapper {
      @media (min-width: 1020px){
      border: 2px solid blue;
      right: 220px;
      display:flex;
      width: 900px;
      height: 25px;
      justify-content: space-between;
      }
    }

    a {
    font-family: 'Playfair Display', serif;
    font-size: 20px;
    text-decoration: none;
    color: black;

    @media (min-width: 1020px) {
    font-family: 'PT Sans', sans-serif;
    text-transform: uppercase;
    font-size: 18px;
    &:hover {
    font-family: 'Playfair Display', serif;
    font-size: 19px;
    }
    }

  }

// MOBIL
// HAMBURGER ICON
.hamburger {
  margin-right: 15px;
  display: none;
