import React from 'react'

const GuestBookForm = ( { onFormSubmit, newThought, onSetThoughtChange } ) => {

    return (
       <section className='thoughtform-wrapper'>
        <form onSubmit={onFormSubmit}>

                <h1>How was your visit at Hotel Prins Carl?</h1>
                <label htmlFor='new-thought'>
                    <textarea 
                        className='input-text'
                        onChange={onSetThoughtChange}
                        value={newThought}
                        id='new-thought'
                        name='new-thought'
                        placeholder='Tell us!'
                         />
                        <textarea 
                        className='input-text'
                        onChange={onSetNameChange}
                        value={newName}
                        id='new-name'
                        name='new-name'
                        placeholder='NAME'
                         />
                </label>

                <button 
                    type='submit'
                    className='submit-button'>
                    <span role='img' aria-label='heart'>❤️ Send your greeting ❤️</span>
                </button>
        </form>
       </section>
    );
};

export default GuestBookForm;
