import React from 'react'
import { formatRelative } from 'date-fns'
import styled from 'styled-components';

const GuestBookList = ({thought}) => {

    return (
    <GuestBookListWrapper>
         <MessageCard key={thought._id} >
            <Message>{thought.name}</Message>
            <Message>{thought.message}</Message>
            <div className="likedate-wrapper">
                <p className="date"> {formatRelative(new Date(thought.createdAt), new Date())}</p>
            </div>
        </MessageCard>   
    </GuestBookListWrapper>
    );
};

export default GuestBookList

const GuestBookListWrapper = styled.section`
  border: 2px solid rgb(165, 165, 165);
  display: flex;
  align-items: center;
  justify-content: center;
  height: 150px;
  width: 400px;
  background-color: white;
  margin-top: 20px;
  box-shadow: 6px 6px;
    @media (min-width: 668px) {
    }
`

const MessageCard = styled.form`
  height: 150px;
  width: 90%;
  overflow-wrap: break-word; 
    @media (min-width: 668px) {
    }
`

const Message = styled.p`
  font-size: large;
    @media (min-width: 668px) {
    }
`
