            <option value="Germany">ðŸ‡©ðŸ‡ª</option>
            </SelectCountry>
          <TextAreaCountry 
             className='input-text'
             onChange={onSetCountryChange}
             value={newCountry}
              id='new-thought'
              name='new-thought'
              placeholder='Country'
              />
            </CountryWrapper>
          </div>
                </label>
                <SendButton 
                    type='submit'>
                    Send
                </SendButton>
        </form>
       </GuestBookFormWrapper>
    );
};

export default GuestBookForm;

