import React from 'react'
import { formatRelative } from 'date-fns';

const GuestBookList = ({thought, handleLikes}) => {

    return (
    <section className='thoughtlist-wrapper'>
         <form className='message-card' >
            <p className='messages' key={thought._id}>{thought.name}</p>
            <p className='messages' key={thought._id}>{thought.message}</p>
            <div className="likedate-wrapper">

                <div className="like">
                    <button 
                        className={thought.likes > 0 ? 'button-heart clicked' : 'button-heart'}
                        onClick={() => handleLikes(thought._id)}>
                        <span role='img' aria-label='heart'>❤️</span>
                    </button>
                    <span className='like-counter'> x {thought.likes}</span>
                </div>
                <p className="date"> {formatRelative(new Date(thought.createdAt), new Date())}</p>
            </div>
        </form>   
    </section>
    );
};

export default GuestBookList
