import React from 'react'
import styled from 'styled-components/macro';

const Spinner = () => (
  <LoadingGuestbook>
    <LoadingGuestbookSpinner>
    ❤️
    </LoadingGuestbookSpinner>
    <p>Loading guestbook...</p>
  </LoadingGuestbook>
)

export default Spinner

const LoadingGuestbook = styled.div`
  margin: auto;
  margin-top: 30px;
  margin-bottom: 30px;
  border: 1px black solid;
  padding: 15px;
  padding-top: 3px;
  background-color: #F2F0F0;
  box-shadow: 5px 7px 0px 0px black;
  width: 350px;
  text-align: center;
  font-family: 'Roboto Mono', monospace;
  font-size: 0.9em;
  `

  const LoadingGuestbookSpinner = styled.div`
animation-name: loadspinner;
  display: block;
  width: 40px;
  height: 40px;
  font-size: 40px;
  margin-top: 30px;
  margin: 30px auto;
  animation-duration: 800ms;
  animation-iteration-count: infinite;
  animation-timing-function: linear;
  `
