import React from 'react';
import Logo from '../images/prinscarl_logo.png'
import styled from 'styled-components'

const Header = () => {

    return (
    <Wrapper>
    <Image src={Logo} alt="Prins Carl Logo" />
    <ContactButton>Contact us</ContactButton>
    <NavMenu>
    <input type="checkbox" id="checkbox_toggle" />
    <label for="checkbox_toggle" className="hamburger">&#9776;</label>
    <div className="menu">
      <a href="#">Book</a>
      <a href="#">Events</a>
      <a href="#">info</a>
      <a href="/Summary">Book</a>
    </div>
    </NavMenu>
    </Wrapper>
    )
}

export default Header;

const ContactButton = styled.button`
    display: block;
    position: absolute;
    top: 10px;
    right:10%;
    @media (min-width: 668px) {
    display: none;
    }
`

const Wrapper = styled.div`
    border: 2px solid hotpink;
    background-color: white;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 40px;
    @media (min-width: 668px) {
    width: 80%;
    margin-left:10%;
    height: 100px;
    }
`

const Image = styled.img`
height: 30px;
@media (min-width: 668px) {
height: 70px;
}
`

const NavMenu = styled.nav`
    border: 3px solid yellow;
    width: 80%;
  a {
    font-family: 'Playfair Display', serif;
    font-size: 25px;
    margin-left: 20px;
    text-decoration: none;
    color: black;
    &:hover {
    color: grey;
    }
  }
.hamburger {
  width: 50px;
  display: none;
  font-size: 18px;
  user-select: none;
  }
  input[type=checkbox] {
  display: none;
  }
@media (max-width: 750px) {
  .menu {
  display: none;
  position: absolute;
  background-color: #FCF8E3;
  border: 2px solid black;
  right: 10%;
  padding: 16px;
  a{
  display: block;
  margin-bottom: 10px;
  &:hover {
  display: inline-block;
  width: 100px;
  height: 40px;
  position: relative;
  background: red;
  &:hover:after{
  content: "";
  position: absolute;
  left: 0;
  bottom: 0;
  width: 0;
  height: 0;
  border-left: 20px solid white;
  border-top: 20px solid transparent;
  border-bottom: 20px solid transparent;
  }
   &:hover:before{
  content: "";
  position: absolute;
  right: -20px;
  bottom: 0;
  width: 0;
  height: 0;
  border-left: 20px solid red;
  border-top: 20px solid transparent;
  border-bottom: 20px solid transparent;
  }
  }
  }

  }
 .hamburger {
  display: block;
  position: absolute;
  top: 10px;
  right:5%
  }
  input[type=checkbox]:checked ~ .menu{
  display: block;
  position: absolute;
  top: 80px;
  height: 200px;
  width:150px;
  flex-direction: column;
  text-align: left;
  }
}
`
